shader.vertex:
#version 150

#if __VERSION__ >= 130
  #define attribute in
  #define varying out
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

attribute vec4 	positions;
attribute vec2 	uvs;
varying vec2 	v_texCoord;

uniform mat4 worldToCameraMatrix , projectionMatrix , localToWorldMatrix  ;


void main(void)
{
	gl_Position = projectionMatrix * worldToCameraMatrix * localToWorldMatrix  * positions;
	v_texCoord = uvs;
}

shader.fragment:
#version 150

//#extension GL_EXT_gpu_shader4 : enable

#if __VERSION__ >= 130
  #define varying in
  out vec4 mgl_FragColor;
  #define texture2D texture
  #define gl_FragColor mgl_FragColor
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

varying vec2 		v_texCoord ;
uniform sampler2D 	_MainTex ;
uniform vec2        _MainTex_Scale;
uniform vec2        _MainTex_Offset;
uniform vec4 		baseColor ;

void main (void)
{
	vec4 c = texture2D(_MainTex , v_texCoord * _MainTex_Scale + _MainTex_Offset) ;
	gl_FragColor = vec4( baseColor.r * c.a, baseColor.g* c.a, baseColor.b * c.a, baseColor.a * c.a ) ;
	//gl_FragColor = vec4(0.8) ;x
}
