shader.vertex:
#version 150

uniform mat4 worldToCameraMatrix , projectionMatrix ;
in vec2 	uvs ;
in vec4     positions;
out vec2    v_texCoord;

void main()
{
    gl_Position = projectionMatrix * worldToCameraMatrix * positions ;
    v_texCoord = uvs;
}

shader.fragment:
#version 150

uniform sampler2D 	_MainTex ;
in vec2 		    v_texCoord ;

out vec4            outColor;

void main()
{
//    outColor = vec4(1.0, 0.0, 1.0, 1.0) ;
    outColor = texture2D( _MainTex , v_texCoord ) ;
}
