shader.vertex:
#version 150

#if __VERSION__ >= 130
  #define attribute in
  #define varying out
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

attribute vec4 	positions;
attribute vec4 colors;
attribute vec2 	uvs;
attribute float alphas;
varying vec2 	v_texCoord;
varying vec4 v_color;
varying float v_alpha;

uniform mat4 worldToCameraMatrix , projectionMatrix , localToWorldMatrix  ;


void main(void)
{
	gl_Position = projectionMatrix * worldToCameraMatrix * localToWorldMatrix  * positions;
	v_texCoord = uvs;
	v_color = colors;
	v_alpha = alphas;
}

shader.fragment:
#version 150

//#extension GL_EXT_gpu_shader4 : enable

#if __VERSION__ >= 130
  #define varying in
  out vec4 mgl_FragColor;
  #define texture2D texture
  #define gl_FragColor mgl_FragColor
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

varying vec2 		v_texCoord ;
varying vec4        v_color;
uniform sampler2D 	_MainTex ;
uniform vec4 		baseColor ;
uniform vec4 		testColor ;//TEST SELECTION
varying float       v_alpha;

void main (void)
{
    //always red and ignore opacity
    gl_FragColor = vec4(1.0,0.0,0.0,0.5);
}
