shader.vertex:
#version 150

#if __VERSION__ >= 130
  #define attribute in
  #define varying out
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

uniform mat4 worldToCameraMatrix , projectionMatrix ;
uniform mat4 localToWorldMatrix ;// TEST IMPL

attribute vec4 positions;
attribute vec4 colors;

varying vec4	v_color ;

void main(void)
{
 	gl_Position = projectionMatrix * worldToCameraMatrix * localToWorldMatrix * positions ;
 	v_color = colors ;
}

shader.fragment:
#version 150

//#extension GL_EXT_gpu_shader4 : enable

#if __VERSION__ >= 130
  #define varying in
  out vec4 mgl_FragColor;
  #define gl_FragColor mgl_FragColor
#endif

#ifdef GL_ES
precision mediump float;
precision mediump int;
#endif

varying vec4	v_color ;
uniform vec4	innerColor ;
uniform vec4	outerColor ;
uniform vec4	pointSteps ; // 色を段階的に切り替えるために渡す


void main (void)
{
	float d = max( abs(gl_PointCoord.x - 0.5) , abs(gl_PointCoord.y - 0.5) );

	float a = smoothstep( pointSteps[0] , pointSteps[1] , d ) ;
	float w = smoothstep( pointSteps[2] , pointSteps[3] , d ) ;
	gl_FragColor = (outerColor + (innerColor-outerColor)*w)*a ;
}
